const mysql = require("mysql");
const util = require("util");
const aws = require("aws-sdk");

var ssm = new aws.SSM({ region: "ap-southeast-1" });

var options = {
  Name: "/app/cinema" /* required */,
  WithDecryption: true,
};
var params = ssm.getParameter(options).promise();
var p = params.then(function (data, err) {
  if (err) {
    console.log(err, err.stack);
  } // an error occurred
  else {
    console.log(JSON.parse(data.Parameter.Value), "_____1");
    return JSON.parse(data.Parameter.Value);
  } // successful response
});
console.log(p, "_____2");

var pool = mysql.createPool({
  host: p.host,
  user: p.user,
  password: p.password,
  database: p.database,
  port: p.port,
});

const apiResponse = (status, msg) => {
  return {
    statusCode: status,
    body: JSON.stringify({
      message: msg,
    }),
  };
};

export const handler = async (event, context, callback) => {
  const connQueryPromisified = util.promisify(pool.query).bind(pool);
  const result = await connQueryPromisified("SELECT * FROM Movie;");
  return apiResponse(200, result);
};
