const mysql = require("mysql");
const util = require("util");
const aws = require("aws-sdk");

var ssm = new aws.SSM({ region: "ap-southeast-1" });

var options = {
  Name: "/app/cinema" /* required */,
  WithDecryption: true,
};

var params = async () => {
  return await ssm.getParameter(options).promise();
};
console.log(params, "______payload");
let p = JSON.parse(params);
var pool = mysql.createPool({
  host: p.host,
  user: p.user,
  password: p.password,
  database: p.database,
  port: p.port,
});

const apiResponse = (status, msg) => {
  return {
    statusCode: status,
    body: JSON.stringify({
      message: msg,
    }),
  };
};

export const handler = async (event, context, callback) => {
  const connQueryPromisified = util.promisify(pool.query).bind(pool);
  const result = await connQueryPromisified("SELECT * FROM Movie;");
  return apiResponse(200, result);
};
